package LineExpressions11;

public class lineinieVirazhenia11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a = 12;
		double b = 13;
		double p;
		double s;

		p = a + b + Math.sqrt(a * a + b * b);
		s = a * b / 2.0;
		System.out.println("S = " + s);
		System.out.println("P = " + p);

	}

}
