package LineExpressions15;

public class lineinieVirazhenia15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = Math.PI;
		for(double i = 1; i <= 4; i++){
			x = (double)Math.pow(x, i);
			System.out.println(x + " в степени " + i + " = " + x);
	}

}}
