package LineExpressions8;

public class lineinieVirazhenia8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       double b = 10;
       double a = 2;
       double c = 4;
       double x;
       x = b + Math.sqrt(b + Math.pow(b, 2)+4*a*c)/2*a - Math.pow(a, 3)+ c+ Math.pow(b, -2); 
       System.out.println("X = "+ x);
       
	}

}
